import { useState } from "react";
import { asyncPostATweet } from "./store/tweet";
import { useDispatch } from "react-redux";

const CreateTweet = () => {
    const [tweetMassage, setTweetMassage] = useState("");
    const dispatch = useDispatch();

    const handeSubmit = (e) => {
        e.preventDefault();
        if (tweetMassage) {
            dispatch(asyncPostATweet({message: tweetMassage}));
        }
        setTweetMassage("");
    }
    return (
        <>
            <form onSubmit={handeSubmit}>
                <label>Tweet</label>
                <input value={tweetMassage} onChange={(e) => setTweetMassage(e.target.value)}></input>
                <button type="submit">Submit</button>
            </form>
        </>
    )
}

export default CreateTweet;
